mapper.select:
SELECT p.postId, u.uid, p.content, p.postedDate, p.viewCount, p.likeCount FROM postTbl p INNER JOIN userTbl u ON p.uid = u.uid WHERE 1=1
AND u.uid LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%') OR p.postedDate LIKE CONCAT('%', #{keyword}, '%')
ORDER BY p.postedDate desc LIMIT #{pageStart}, #{perPageNum}
